   const inputEvent= () =>{
        setName(Event.target.value);
    }
    const onSubmit =() =>{
        setFullName(name)
    }
    return (
<>
<h1>Hello {fullname}</h1>
<div className='newDiv'>
<input type='text' placeholder='Enter your name' onChange={inputEvent} value={name}></input>
</div>
<button onClick={onSubmit}>Click Me üëç</button>
</>
    );
};

export default App;
