// Using Hooks for printing the current time on clicking the button

import React, { useState } from 'react';

let newtime = new Date().toLocaleTimeString();
const [curTime,setCurTime] = useState(newtime);
const ctime = () =>{
newtime=new Date().toLocaleTimeString();
    setCurTime(newtime);
}

const App = () =>{

    return(
        <>
        <h1>{newtime}</h1>
        <button onClick={ctime}>Get Time</button>
        </>
    );
}
export default App;
