import NewsUpdate from './NewsUpdate';
import React, { Component } from 'react';
import Spinner from './Spinner';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      category: 'business', // Default category
    };
  }

  async fetchNews() {
    this.setState({ loading: true });
    const apiKey = '89bfa66a3fed4046b950d4d76fc7da56';
    let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.state.category}&apiKey=${apiKey}&page=${this.state.page}&pageSize=12`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({ articles: parsedData.articles, loading: false });
  }

  async componentDidMount() {
    this.fetchNews();
  }

  handleCategoryChange = (category) => {
    this.setState({ category, page: 1 }, this.fetchNews);
  };

  nextClick = async () => {
    this.setState({ page: this.state.page + 1 }, this.fetchNews);
  };

  previousClick = async () => {
    this.setState({ page: this.state.page - 1 }, this.fetchNews);
  };

  render() {
    return (
      <div className='container my-3'>
        <div className='container d-flex justify-content-between'>
          <button disabled={this.state.page <= 1} type="button" className='btn btn-dark' onClick={this.previousClick}>Previous</button>
          <button type='button' className='btn btn-dark' onClick={this.nextClick}>Next</button>
        </div>
        <h2 className='text-center'>TOP HEADLINES</h2>
        <div className="d-flex justify-content-center my-3">
          <button className={`btn ${this.state.category === 'business' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => this.handleCategoryChange('business')}>Business</button>
          <button className={`btn mx-2 ${this.state.category === 'technology' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => this.handleCategoryChange('technology')}>Technology</button>
          <button className={`btn ${this.state.category === 'education' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => this.handleCategoryChange('education')}>Education</button>
        </div>
        {this.state.loading && <Spinner />}
        <div className='row'>
          {this.state.articles.map((element) => {
            return (
              <div className='col-md-4' key={element.url}>
                <NewsUpdate
                  title={element.title}
                  newsUrl={element.url}
                  description={element.description ? element.description.slice(0, 80) : "No description available."}
                  imgUrl={element.urlToImage}
                />
              </div>
            );
          })}
        </div>
        {this.state.loading && <Spinner />}
        <div className='container d-flex justify-content-between'>
          <button disabled={this.state.page <= 1} type="button" className='btn btn-dark' onClick={this.previousClick}>Previous</button>
          <button type='button' className='btn btn-dark' onClick={this.nextClick}>Next</button>
        </div>
      </div>
    );
  }
}

export default News;
